#Optional bounding class - again unused
def bound(x, m, M=None):
    """Either have m as scalar, so bound(x,m,M) which returns m <= x <= M *OR*
    have m as length 2 vector, bound(x,m, <IGNORED>) returns m[0] <= x <= m[1].

    Args:
        x: scalar
        m: The lower bound
        M: The upper bound

    Returns:
        x: scalar, bound between min (m) and Max (M)
    """
    if M is None:
        M = m[1]
        m = m[0]
    # bound x between min (m) and Max (M)
    return min(max(x, m), M)



def DymolaDyn(self, y0 , t,  variables, results, fflist):
    """
    Dymola dynamics for the time step - sets the feedforward component of the PID controller in the dsin file 
    then simulates 5 seconds of the dynamics
    """
    
    ff = y0[-1] + y0[-2]
    
    fflist.append(ff)
    
    #print(fflist)
    
    changedvars = {'FeedForward.k':ff} 

    oriInputFiles = ["dsfinal.txt"]
    currentInputFiles = ["dsfinalnewFF.txt"]

    newinput = createNewInput(currentInputFiles, oriInputFiles, changedvars)

    os.system('./dymosim ' + newinput[0] + ' >/dev/null 2>&1')

    matSourceFileName = "dsres.mat"

    response = matreader(matSourceFileName,variables)

    for i in range(0,len(variables),1):
        results[variables[i]].extend(response[self.variables[i]])

    Tout = results["BOP.sensor_pT.T"][-1]
    PumpMFlow = results["pump_SimpleMassFlow1.m_flow"][-1]
    FF = results["FeedForward.y"][-1]
    
    Qdemand = self.y.get(t+2*self.dt)
    # print(Qdemand)

    terminal = False
    
    yout = [Tout, PumpMFlow, Qdemand, FF]
    print(yout)

    return yout, results, terminal
