def DymolaDyn(self, y0, reward_price_t, reward_price_t1, reward_price_t2, prevDNI, DNI, prevIAM, IAM,DNI_avg):
    """
    Dymola dynamics for the time step - sets the feedforward component of the PID controller in the dsin file 
    then simulates 5 seconds of the dynamics
    """

    #generate new input

    Offset = y0[0]
    
    pd = (y0[-2]*1e6)+49e6

    dpd = pd - Offset

    dDNI = DNI - prevDNI

    dIAM = IAM - prevIAM

    rampduration = 3600

    duration = y0[-1]

    Offset = y0[0]

    if (pd) > 57.1e6:
        dpd  = 57.1e6 - Offset
    
    if (pd) < 48.9e6:
        dpd  = 48.9e6 - Offset

    starttime = self.t + 1003600 + 1
    
    changedvars = {'PowerDemand.offset':Offset,'PowerDemand.startTime':starttime,'PowerDemand.height':dpd, 'PowerDemand.duration':duration,
                   'CosEff_Const.offset':prevIAM,'CosEff_Const.startTime':starttime,'CosEff_Const.height':dIAM, 'CosEff_Const.duration':rampduration,
                   'DNI_Const.offset':prevDNI,'DNI_Const.startTime':starttime,'DNI_Const.height':dDNI, 'DNI_Const.duration':rampduration} 

    oriInputFiles = ["dsfinal.txt"]
    currentInputFiles = ["dsfinalnewFF.txt"]

    newinput = createNewInput(currentInputFiles, oriInputFiles, changedvars)

    #run dymosim

    os.system('./dymosim ' + newinput[0]+ ' >/dev/null 2>&1')

    #get trajectory and determine if outside limits

    terminal = False

    matSourceFileName = "dsres.mat"

    response = matreader(matSourceFileName,self.variables)

    for i in range(0,len(self.variables),1):
        self.results[self.variables[i]].extend(response[self.variables[i]])

    c_penalty = False

    for value in response["dual_Pipe_CTES_Controlled_Feedwater.CTES.T_Ave_Conc"]:
        if value < 438.5 or value > 444.5:
            #print("Terminating Because Concrete Temperature Outside Limits")
            terminal = True
            c_penalty = True
        if value < 439.5 or value > 443.5:
            #print("Terminating Because Concrete Temperature Outside Limits")
            c_penalty = True

    p_penalty = False
    t_penalty = False

    for value in response["BOP.sensor_T2.T"]:
        if value < 147.5 + 273.15 or value > 148.5 + 273.15:
            t_penalty = True
        if value < 146 + 273.15 or value > 150 + 273.15:
            #print("Terminating Because Feedwater Temperature Outside Limits")
            terminal = True

    for value in response["BOP.deaerator.medium.p"]:
        if value < 111000 or value > 161000:
            p_penalty = True
        if value < 101000 or value > 181000:
            #print("Terminating Because Deaerator Pressure Outside Limits")
            terminal = True

    #get observation 

    yout = getState(response, reward_price_t, reward_price_t1, reward_price_t2, DNI, DNI_avg)

    return yout, terminal, p_penalty, t_penalty, c_penalty

